CREATE TABLE PLAYER_ARCHIVE(PLAYER_ID BIGINT NOT NULL,NAME VARCHAR DEFAULT '' NOT NULL,SURNAME VARCHAR DEFAULT '' NOT NULL,COUNTRY_ID INTEGER DEFAULT 0 NOT NULL,TEAM_ID INTEGER DEFAULT 0 NOT NULL,AGE INTEGER DEFAULT 0 NOT NULL,TRANSFER_LIST INTEGER DEFAULT 0 NOT NULL,VALUE INTEGER DEFAULT 0 NOT NULL,WAGE INTEGER DEFAULT 0 NOT NULL,CARDS INTEGER DEFAULT 0 NOT NULL,GOALS INTEGER DEFAULT 0 NOT NULL,ASSISTS INTEGER DEFAULT 0 NOT NULL,MATCHES INTEGER DEFAULT 0 NOT NULL,NT_CARDS INTEGER DEFAULT 0 NOT NULL,NT_MATCHES INTEGER DEFAULT 0 NOT NULL,NT_ASSISTS INTEGER DEFAULT 0 NOT NULL,NT_GOALS INTEGER DEFAULT 0 NOT NULL,INJURY_DAYS DOUBLE DEFAULT 0.0E0 NOT NULL,SKILL_FORM INTEGER DEFAULT 0 NOT NULL,SKILL_EXPERIENCE INTEGER DEFAULT 0 NOT NULL,SKILL_TEAMWORK INTEGER DEFAULT 0 NOT NULL,SKILL_DISCIPLINE INTEGER DEFAULT 0 NOT NULL,NATIONAL INTEGER DEFAULT 0 NOT NULL,YOUTH_TEAM_ID INTEGER DEFAULT 0 NOT NULL,EXISTS_IN_SOKKER INTEGER DEFAULT 0 NOT NULL, NOTE VARCHAR DEFAULT '' NOT NULL,CONSTRAINT PLAYER_ARCHIVE_PKEY PRIMARY KEY(PLAYER_ID))
ALTER TABLE reports ADD COLUMN checked BOOLEAN DEFAULT false NOT NULL;
INSERT INTO player_archive (player_id, name, surname, country_id, team_id, transfer_list, national, youth_team_id, exists_in_sokker) SELECT id_player, name, surname, countryfrom, id_club_fk, transfer_list, national, youth_team_id,1 FROM player WHERE id_player NOT IN (SELECT player_id FROM player_archive);
DELETE FROM player WHERE status = 20;
